'use strict';

var _require = require('..'),
    Decade = _require.Decade;

describe('Decade', function () {

  describe('bounds', function () {
    it('min', function () {
      expect(new Decade(199).min).to.eql(Date.UTC(1990, 0, 1, 0, 0, 0, 0));
    });

    it('max', function () {
      expect(new Decade(199).max).to.eql(Date.UTC(1999, 11, 31, 23, 59, 59, 999));
    });
  });

  describe('.edtf', function () {
    it('default', function () {
      return expect(new Decade().edtf).to.match(/^\d\d\d$/);
    });

    it('DDD', function () {
      expect(new Decade([198]).edtf).to.eql('198');
      expect(new Decade(99).edtf).to.eql('099');
    });

    it('-DDD', function () {
      expect(new Decade(-9).edtf).to.eql('-009');
    });

    it('DDD~', function () {
      expect(new Decade({ values: [198], approximate: true }).edtf).to.eql('198~');
    });
  });
});