'use strict';

var _require = require('..'),
    List = _require.List;

describe('List', function () {

  describe('.edtf', function () {
    it('default', function () {
      return expect(new List().edtf).to.eql('{}');
    });

    it('{YYYY}', function () {
      expect(new List(['2016']).edtf).to.eql('{2016}');
    });

    it('{YYYY..YYYY}', function () {
      expect(new List([['2016', '2018']]).edtf).to.eql('{2016..2018}');
    });
  });
});