'use strict';

var _require = require('..'),
    Set = _require.Set;

describe('Set', function () {

  it('type', function () {
    expect(Set.type).to.eql('Set');
    expect(new Set().type).to.eql('Set');
  });

  it('.from()', function () {
    expect(Set.from('[2016]')).to.be.instanceof(Set);
  });

  describe('.edtf', function () {
    it('default', function () {
      return expect(new Set().edtf).to.eql('[]');
    });

    it('[YYYY]', function () {
      expect(new Set(['2016']).edtf).to.eql('[2016]');
    });

    it('[..YYYY]', function () {
      expect(new Set({ values: ['2016'], earlier: true }).edtf).to.eql('[..2016]');
      expect(new Set({ values: [], earlier: true }).edtf).to.eql('[]');
    });

    it('[YYYY..]', function () {
      expect(new Set({ values: ['2016'], later: true }).edtf).to.eql('[2016..]');
      expect(new Set({ values: [], later: true }).edtf).to.eql('[]');
    });

    it('[YYYY..YYYY]', function () {
      expect(new Set([['2016', '2018']]).edtf).to.eql('[2016..2018]');
    });
  });
});